import java.util.*;
public class Maths {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.pow(2, 3));

		// Displays 8.0

		System.out.println(Math.pow(4, 0.5));

		// Displays 2.0

		System.out.println(Math.pow(2.5, 2));

		// Displays 6.25

		System.out.println(Math.pow(2.5, -2));

		// Displays 0.16

}}
