import java.util.*;
public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double radius;
		double area;
		radius = 1.0;

		area = radius * radius * 3.14159;

		System.out.println("The area is " + area + " for radius " + radius);

		// Compute the second area

		radius = 2.0;

		area = radius * radius * 3.14159;

		System.out.println("The area is " + area + " for radius "+radius);
	}

}
